export default function TodoForm(props){


// Create a TodoForm component to obtain input from user:

//     Store local state for input
//     Create event handler for form (either onClick of button or onSubmit of form) to validate and call addTodo function
//     Validate an empty todo to prevent adding to todos 




    // local state variable - defined inside of TodoForm


  return (
    <div className="App">
      <h3> Todo App </h3> 
      <input type="text" value="Add Todo" onclick="return Todo();" />
      <br></br>
      <input type="submit" value="Add Todo" onclick="return Todo();" />
        <TodoList todos = {props.todos} completeTodo={props.completeTodo} deleteTodo={props.deleteTodo}/>
    </div>
  );
}
